import React, { Component } from 'react'




export  class Categorie extends Component {

    
    state = {
    loading: true,
    cat: null,
    };

    async    componentDidMount(){
    const url = "https://localhost:44318/api/Cat%C3%A9gories";
    const response = await fetch(url);
    const data = await response.json();
    this.setState({cat: data.Catégorie,loading: false});
    }

    render() {
       

        return (
            <div>{this.state.loading}|| !this.state.cat ?(
                <div>loading ...</div>
            ):(
               <div>
                   <div>{this.state.cat.id}</div>
                   <div>{this.state.cat.nameCatégorie}</div>
                   <div>{this.state.cat.imageCatégorie}</div>
               </div>
               )}
               </div>
        );
    }
}

export default Categorie
