import React, { Component } from 'react'
import { Table } from 'react-bootstrap';



export class Categorie extends Component {


    constructor(props){
        super(props);
        this.state={deps:[]}
    }

    refreshList(){
        fetch(process.env.REACT_APP_API+'Catégorie')
        .then(response=>response.json())
        .then(data=>{
            this.setState({deps:data})
        })
    }

    componentDidMount(){
        this.refreshList();
    }

    componentDidUpdate(){
        this.refreshList();
    }

    render() {
        const {deps}=this.state;

        return (
            <div>
                <Table striped bordered hover>
                    <thead>
                        <tr>
                            <th>id</th>
                            <th>name</th>
                            <th>image</th>
                        </tr>
                    </thead>
                    <tbody>
                        {deps.map(dep=>
                        <tr key={dep.Id}>
                            <td>{dep.Id}</td>
                            <td>{dep.NameCatégorie}</td>
                            <td>{dep.ImageCatégorie}</td>
                        </tr>
                        )}
                    </tbody>
                </Table>
            </div>
        )
    }
}

export default Categorie
