export  class Categorie extends Component {

    constructor(props){
        super(props);
        this.state={produits:[]}
    }

    refreshList(){
        fetch("https://localhost:44318/api/produits")
        .then(response=>response.json())
        .then(data=>{
            this.setState({produits:data});
        });
    }

    componentDidMount(){
        this.refreshList();
    }

    componentDidUpdate(){
        this.refreshList();
    }


    render() {
        const {produits}=this.state;
        return (
        <Table className="mt-4" striped bordered hover size="sm">
                    <thead>
                        <tr>
                            <th>import {  } from 'module'</th>
                            <th>Name</th>
                            <th>prix</th>
                            <th>DOJ</th>
                            <th>Options</th>
                        </tr>
                    </thead>
                    <tbody>
                        {produits.map(produit=>
                            <tr key={produit.EmployeeId}>
                                <td>{produit.EmployeeId}</td>
                                <td>{produit.EmployeeName}</td>
                                <td>{produit.Department}</td>
                                <td>{produit.DateOfJoining}</td>
                                <td></td>
                            </tr>
                        )}
                    </tbody> 
        </Table>
        )
    }
}
export default Categorie
