export  class Categorie extends Component {

    constructor(props){
        super(props);
        this.state={produits:[]}
    }

    refreshList(){
        fetch("https://localhost:44318/api/produits")
        .then(response=>response.json())
        .then(data=>{
            this.setState({produits:data});
        });
    }

    componentDidMount(){
        this.refreshList();
    }

    componentDidUpdate(){
        this.refreshList();
    }


    render() {

        return null;
    }
}
export default Categorie
