import React, { Component } from 'react';
import {Table} from 'react-bootstrap';




export  class Book extends Component {

    constructor(props){
        super(props);

        this.state={
            Books:[]
        };
    }

    componentDidMount(){
       fetch('https://localhost:44305/api/Books').then(res=>res.json()).then(
           result=>{
               this.setState({Books:result});
           }
       )
    }

    render() {
        return (
        <Table className="mt-4" striped bordered hover size="sm">
            <thead>
                <tr>
                    <th>id</th>
                    <th>title</th>
                    <th>author</th>
                    <th>description</th>
                </tr>
            </thead>
             <tbody>
                {this.state.Books.map(Produit=>
                    <tr key={Books.id}>
                        <td>{Books.id}</td>
                        <td>{Books.title}</td>
                        <td>{Books.author}</td>
                        <td>{Books.description}</td>
                   </tr>
                )}
            </tbody>                
        </Table>
        )
    }
}
export default Book
